def decrypt(message: str) -> str:
    """
    Function to decrypt the spy's messages. Takes `message`, an input string
    and returns a string with the decrypted message.  
    """
    message_decrypted = ""
    message_dict = {}
    message_list = message.split()

    # loop and store in dictionary
    for m in message_list:
        m = m.lower()
        new_val = message_dict.get(m, 0) + 1 
        message_dict[m] = new_val

    # join dictionary keys and values into output array
    for k in message_dict.keys():
        message_decrypted = f"{message_decrypted}{k}{message_dict[k]}"
    return message_decrypted  


